import React, { useRef, useEffect } from "react";
import styled from "styled-components";
import {
  EducationDetail,
  FrameworkSkillContent,
  PersonalDetail,
  ProgrammingSkillContent,
  SoftSkillContent,
  Work,
} from "../common/Content";
import Card from "../components/Card";
import ListCard from "../components/ListCard";
import "./Home.css";

const SkillWrapper = styled.div`
  display: flex;
  justify-content: space-between;
  margin-bottom: 16px;
`;

const HandleClick = () => {
  window.scrollTo({ top: document.body.scrollHeight, behavior: "smooth" });
};

export default function Home() {
  const skillCardRef = useRef(null);

  useEffect(() => {
  }, []);

  const observer = new IntersectionObserver((entries) => {
    entries.forEach((entry) => {
      const square = entry.target.querySelector(".ListCard");

      if (entry.isIntersecting) {
        square.classList.add("square-animation");
        return; // if we added the class, exit the function
      }
    });
  });

  observer.observe(document.querySelector(skillCard));

  return (
    <>
      <Card img="profile_picture.png" content={PersonalDetail} border />
      <SkillWrapper ref={skillCard}>
        <ListCard
          border
          contents={ProgrammingSkillContent}
          title="Programming Skill"
        />
        <ListCard
          border
          contents={FrameworkSkillContent}
          title="Framework Skill"
        />
        <ListCard border contents={SoftSkillContent} title="Soft Skill" />
      </SkillWrapper>
      <SkillWrapper>
        <ListCard
          title="Bachelor of Engineering"
          contents={EducationDetail}
          border
        />
        <ListCard
          title="Work Experiences"
          handleClick={HandleClick}
          contents={Work}
          border
        />
      </SkillWrapper>
      <SkillWrapper>
        <ListCard
          border
          contents={ProgrammingSkillContent}
          title="Programming Skill"
        />
        <ListCard
          border
          contents={FrameworkSkillContent}
          title="Framework Skill"
        />
        <ListCard border contents={SoftSkillContent} title="Soft Skill" />
      </SkillWrapper>
    </>
  );
}

